mapper.select:
select

from t_xc_driver
where is_delete=0 and  school_id=#{schoolId}

	and driver_name like concat('%',#{driverName},'%')


	and driver_tel like concat('%',#{driverTel},'%')


	ORDER BY ${orderBy} ${orderType}

	 select 

  from t_xc_driver
  where is_delete=0 and license_no = #{licenseNo}

	 select 

  from t_xc_driver
  where is_delete=0 and id_no = #{idNo}

select 

from t_xc_driver
where is_delete=0 and id = #{id,jdbcType=BIGINT}
